import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const Signup = () => {
  // State variables for user input
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  // Using "role" as the field name to match backend expectation
  const [role, setRole] = useState("");

  const navigate = useNavigate();

  // Function to handle signup form submission
  const handleSignup = async (e) => {
    e.preventDefault();

    try {
      // Send the request to the backend
      const response = await axios.post("http://127.0.0.1:8000/api/auth/signup/", {
        username,
        password,
        role, // Backend uses this to assign a group
      });

      // Check if the response has a token
      if (response.data.token) {
        // Save token and user info in localStorage
        localStorage.setItem("user_id", response.data.user_id);
        localStorage.setItem("username", response.data.username);
        localStorage.setItem("role", response.data.role);
        localStorage.setItem("token", response.data.token);

        alert("Signup Successful!");
        navigate("/dashboard"); // Redirect to dashboard after successful signup
      } else {
        alert("Signup failed: No token received.");
      }
    } catch (error) {
      console.error("Signup Error:", error.response?.data?.message || error.message);
      alert(error.response?.data?.message || "Signup failed! Please try again.");
    }
  };

  return (
    <div
      className="d-flex align-items-center justify-content-center"
      style={{ minHeight: "100vh", background: "linear-gradient(135deg,rgb(16, 92, 167), #e9ecef)" }}
    >
      <div className="card p-4 shadow" style={{ width: "100%", maxWidth: "400px" }}>
        <h2 className="text-center mb-4">Register</h2>
        <form onSubmit={handleSignup}>
          <div className="mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <select
              className="form-control"
              required
              value={role}
              onChange={(e) => setRole(e.target.value)}
            >
              <option value="">Select Role</option>
              <option value="Admin">Admin</option>
              <option value="Staff">Staff</option>
              <option value="Student">Student</option>
            </select>
          </div>
          <button className="btn btn-success w-100" type="submit">
            Register
          </button>
        </form>
        <div className="mt-3 text-center">
          Already have an account?{" "}
          <Link to="/login" className="btn btn-link">
            Login
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Signup;
