import { useState, useEffect } from "react";

const MarkEdit = ({ match }) => {
  const [mark, setMark] = useState({});
  const [subject, setSubject] = useState("");
  const [marksObtained, setMarksObtained] = useState("");
  const [totalMarks, setTotalMarks] = useState("");

  useEffect(() => {
    fetch(`/api/marks/${match.params.id}/`)
      .then((response) => response.json())
      .then((data) => {
        setMark(data);
        setSubject(data.subject);
        setMarksObtained(data.marks_obtained);
        setTotalMarks(data.total_marks);
      });
  }, [match.params.id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = { subject, marks_obtained: marksObtained, total_marks: totalMarks };
    await fetch(`/api/marks/${match.params.id}/`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={subject} onChange={(e) => setSubject(e.target.value)} placeholder="Subject" />
      <input type="number" value={marksObtained} onChange={(e) => setMarksObtained(e.target.value)} placeholder="Marks Obtained" />
      <input type="number" value={totalMarks} onChange={(e) => setTotalMarks(e.target.value)} placeholder="Total Marks" />
      <button type="submit">Update Marks</button>
    </form>
  );
};

export default MarkEdit;
