import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddMarks = () => {
    const [students, setStudents] = useState([]);
    const [selectedStudent, setSelectedStudent] = useState("");
    const [marks, setMarks] = useState({
        subject1: "",
        subject2: "",
        subject3: "",
        subject4: "",
        subject5: "",
    });
    const navigate = useNavigate();

    useEffect(() => {
        const fetchStudents = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get("http://127.0.0.1:8000/api/students/", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setStudents(response.data);
            } catch (error) {
                console.error("Error fetching students:", error.response?.data || error.message);
            }
        };
        fetchStudents();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Check if a student is selected
        if (!selectedStudent) {
            alert("Please select a student.");
            return;
        }

        // Ensure marks are numbers
        const formattedMarks = Object.fromEntries(
            Object.entries(marks).map(([key, value]) => [key, Number(value)])
        );

        try {
            const token = localStorage.getItem("token");
            await axios.post(
                "http://127.0.0.1:8000/api/marks/",
                { student_id: selectedStudent, ...formattedMarks },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert("Marks Added Successfully!");
            navigate("/marks-list");
        } catch (error) {
            console.error("Error adding marks:", error.response?.data || error.message);
            alert("Failed to add marks. Try again.");
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card p-4 shadow">
                        <h2 className="text-center mb-4">Add Marks</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <select
                                    className="form-control"
                                    required
                                    value={selectedStudent}
                                    onChange={(e) => setSelectedStudent(e.target.value)}
                                >
                                    <option value="" disabled>
                                        -- Select a Student --
                                    </option>
                                    {students.length > 0 ? (
                                        students.map((student) => (
                                            <option key={student.id} value={student.id}>
                                                {student.name} (ID: {student.id})
                                            </option>
                                        ))
                                    ) : (
                                        <option disabled>No students found</option>
                                    )}
                                </select>
                            </div>
                            {Object.keys(marks).map((subject, index) => (
                                <div className="mb-3" key={index}>
                                    <input
                                        type="number"
                                        className="form-control"
                                        required
                                        placeholder={`Subject ${index + 1} Marks`}
                                        value={marks[subject]}
                                        onChange={(e) => setMarks({ ...marks, [subject]: e.target.value })}
                                    />
                                </div>
                            ))}
                            <button type="submit" className="btn btn-success w-100">
                                Add Marks
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddMarks;
