// Import required packages
import { useState } from "react"; // React hook to manage state
import axios from "axios"; // Library to make HTTP requests
import { useNavigate } from "react-router-dom"; // React hook to handle navigation

const Login = () => {
    // Declare state variables for username and password
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    
    // Hook for page navigation
    const navigate = useNavigate();

    // Function to handle login
    const handleLogin = async (e) => {
        e.preventDefault(); // Prevent page reload on form submission

        try {
            // Send POST request to login endpoint
            const response = await axios.post("http://127.0.0.1:8000/api/auth/login/", { 
                username, 
                password, 
            });

            // Store token in local storage
            localStorage.setItem("token", response.data.data.token);
            
            // Redirect user to the dashboard
            navigate("/dashboard");
        } catch (error) {
            console.error("Login Error:", error);
            alert(error.response?.data?.message || "Login failed! Please check your credentials.");
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-4">
                    <div className="card p-4 shadow">
                        <h2 className="text-center mb-4">Login</h2>
                        <form onSubmit={handleLogin}>
                            <div className="mb-3">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    required 
                                    placeholder="Username" 
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)} 
                                />
                            </div>
                            <div className="mb-3">    
                                <input 
                                    type="password" 
                                    className="form-control" 
                                    required 
                                    placeholder="Password" 
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)} 
                                />
                            </div>
                            <button className="btn btn-primary w-100" type="submit">
                                Login
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

// Export the Login component
export default Login;
