import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Dashboard = () => {
    const [students, setStudents] = useState([]);
    const [marks, setMarks] = useState([]);
    const [admissions, setAdmissions] = useState([]);
    
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/");
            return;
        }

        const fetchData = async () => {
            try {
                const studentData = await axios.get("http://127.0.0.1:8000/api/students/", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const marksData = await axios.get("http://127.0.0.1:8000/api/marks/", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const admissionData = await axios.get("http://127.0.0.1:8000/api/admissions/", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                setStudents(studentData.data);
                setMarks(marksData.data);
                setAdmissions(admissionData.data);
            } catch (error) {
                console.error("Error fetching data:", error.response?.data || error.message);
                alert("Failed to load data. Check API connection.");
            }
        };

        fetchData();
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem("token");
        navigate("/");
    };

    return (
        <div className="container mt-4">
            <h2 className="text-center mb-4">Dashboard</h2>

            <div className="card p-3 mb-4">
                <h3>Student List:</h3>
                <button className="btn btn-secondary w-25 mt-2" onClick={() => navigate("/add-student")}>
                    Add Student
                </button>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Student ID</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {students.map((student) => (
                            <tr key={student.id}>
                                <td>{student.name}</td>
                                <td>{student.student_id}</td>
                                <td>
                                    <Link className="btn btn-primary btn-sm me-2" to={`/edit-student/${student.id}`}>
                                        Edit
                                    </Link>
                                    <Link className="btn btn-danger btn-sm" to={`/delete-student/${student.id}`}>
                                        Delete
                                    </Link>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div className="card p-3 mb-4">
                <h3>Marks List:</h3>
                <button className="btn btn-secondary w-25 mt-2" onClick={() => navigate("/add-marks")}>
                    Add Marks
                </button>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Student Name</th>
                            <th>Subject 1</th>
                            <th>Subject 2</th>
                            <th>Subject 3</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {marks.map((mark) => (
                            <tr key={mark.id}>
                                <td>{mark.student_name}</td>
                                <td>{mark.subject1}</td>
                                <td>{mark.subject2}</td>
                                <td>{mark.subject3}</td>
                                <td>
                                    <Link className="btn btn-primary btn-sm me-2" to={`/edit-marks/${mark.id}`}>
                                        Edit
                                    </Link>
                                    <Link className="btn btn-danger btn-sm" to={`/delete-marks/${mark.id}`}>
                                        Delete
                                    </Link>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div className="card p-3">
                <h3>Admission List:</h3>
                <button className="btn btn-secondary w-25 mt-2" onClick={() => navigate("/add-admission")}>
                    Add Admission
                </button>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Student Name</th>
                            <th>Admission Date</th>
                            <th>Course</th>
                        </tr>
                    </thead>
                    <tbody>
                        {admissions.map((admission) => (
                            <tr key={admission.id}>
                                <td>{admission.student_name}</td>
                                <td>{admission.admission_date}</td>
                                <td>{admission.course}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <button className="btn btn-danger mt-4" onClick={handleLogout}>
                Logout
            </button>
        </div>
    );
};

export default Dashboard;
