import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Signup = () => {
  // State variables for user input
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  // Using "role" as the field name to match backend expectation
  const [role, setRole] = useState("");

  const navigate = useNavigate();

  // Function to handle signup form submission
  const handleSignup = async (e) => {
    e.preventDefault(); // Prevent default form submission

    try {
      // Send the request to the backend
      const response = await axios.post("http://127.0.0.1:8000/api/auth/signup/", {
        username,
        password,
        role  // Sending the role; backend will use this to assign a group
      });

      // Check if the response has a token
      if (response.data.token) {
        // Save token in localStorage
        localStorage.setItem("token", response.data.token);
        
        alert("Signup Successful!");
        navigate("/dashboard"); // Redirect to dashboard after successful signup
      } else {
        alert("Signup failed: No token received.");
      }
    } catch (error) {
      console.error("Signup Error:", error);
      alert(error.response?.data?.message || "Signup failed! Please try again.");
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-4">
          <div className="card p-4 shadow">
            <h2 className="text-center mb-4">Register</h2>
            <form onSubmit={handleSignup}>
              <div className="mb-3">
                <input 
                  type="text" 
                  className="form-control" 
                  required 
                  placeholder="Username" 
                  value={username}
                  onChange={(e) => setUsername(e.target.value)} 
                />
              </div>
              <div className="mb-3">
                <input 
                  type="password" 
                  className="form-control" 
                  required 
                  placeholder="Password" 
                  value={password}
                  onChange={(e) => setPassword(e.target.value)} 
                />
              </div>
              <div className="mb-3">
                <select 
                  className="form-control" 
                  required 
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                >
                  <option value="">Select Role</option>
                  <option value="Admin">Admin</option>
                  <option value="Staff">Staff</option>
                  <option value="Student">Student</option>
                </select>
              </div>
              <button className="btn btn-success w-100" type="submit">
                Register
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
