import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [students, setStudents] = useState([]);
  const [marks, setMarks] = useState([]);
  const [admissions, setAdmissions] = useState([]);
  
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      try {
        const studentRes = await axios.get("http://127.0.0.1:8000/api/auth/students/", {
          headers: { Authorization: `Token ${token}` },
        });
        const marksRes = await axios.get("http://127.0.0.1:8000/api/auth/marks/", {
          headers: { Authorization: `Token ${token}` },
        });
        const admissionsRes = await axios.get("http://127.0.0.1:8000/api/auth/admissions/", {
          headers: { Authorization: `Token ${token}` },
        });

        setStudents(studentRes.data.slice(0, 5)); // Show only first 5 items
        setMarks(marksRes.data.slice(0, 5));
        setAdmissions(admissionsRes.data.slice(0, 5));
      } catch (error) {
        console.error("Error fetching data:", error.response?.data || error.message);
        alert("Failed to load data. Check API connection.");
      }
    };

    fetchData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">Dashboard</h2>

      {/* Student List Section */}
      <div className="card p-3 mb-4">
        <h3>Students</h3>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Name</th>
              <th>Student ID</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student.StudentId}>
                <td>{student.Name}</td>
                <td>{student.StudentId}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <button className="btn btn-primary me-2" onClick={() => navigate("/students")}>
          View All
        </button>
        <button className="btn btn-success" onClick={() => navigate("/add-student")}>
          Add Student
        </button>
      </div>

      {/* Marks List Section */}
      <div className="card p-3 mb-4">
        <h3>Marks</h3>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Marks ID</th>
              <th>Student</th>
              <th>Subject</th>
            </tr>
          </thead>
          <tbody>
            {marks.map((mark) => (
              <tr key={mark.MarksId}>
                <td>{mark.MarksId}</td>
                <td>{mark.Student}</td>
                <td>{mark.SubjectName}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <button className="btn btn-primary me-2" onClick={() => navigate("/marks")}>
          View All
        </button>
        <button className="btn btn-success" onClick={() => navigate("/add-marks")}>
          Add Marks
        </button>
      </div>

      {/* Admissions List Section */}
      <div className="card p-3 mb-4">
        <h3>Admissions</h3>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Admission ID</th>
              <th>Student</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {admissions.map((admission) => (
              <tr key={admission.AdmissionId}>
                <td>{admission.AdmissionId}</td>
                <td>{admission.Student}</td>
                <td>{admission.Status}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <button className="btn btn-primary me-2" onClick={() => navigate("/admissions")}>
          View All
        </button>
        <button className="btn btn-success" onClick={() => navigate("/add-admission")}>
          Add Admission
        </button>
      </div>

      <button className="btn btn-danger mt-4" onClick={handleLogout}>
        Logout
      </button>
    </div>
  );
};

export default Dashboard;
