import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddMarks = () => {
    const [studentId, setStudentId] = useState("");
    const [marks, setMarks] = useState({
        subject1: "",
        subject2: "",
        subject3: "",
        subject4: "",
        subject5: "",
    });
    const navigate = useNavigate();

    // Function to refresh the JWT token if expired
    const refreshToken = async () => {
        const refreshToken = localStorage.getItem("refresh_token");

        if (!refreshToken) {
            return null;
        }

        try {
            const response = await axios.post(
                "http://127.0.0.1:8000/api/token/refresh/",
                { refresh: refreshToken }
            );
            const { access, refresh } = response.data;
            localStorage.setItem("token", access); // Save the new access token
            localStorage.setItem("refresh_token", refresh); // Save the new refresh token
            return access; // Return the new access token
        } catch (error) {
            console.error("Error refreshing token:", error);
            alert("Please log in again.");
            navigate("/login");
            return null;
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        let token = localStorage.getItem("token"); // Get token from localStorage

        if (!token) {
            alert("You are not authorized. Please log in.");
            navigate("/login");
            return;
        }

        // Validate marks are numbers and not empty
        const isValidMarks = Object.values(marks).every(mark => !isNaN(mark) && mark.trim() !== "");

        if (!isValidMarks) {
            alert("Please enter valid marks for all subjects.");
            return;
        }

        try {
            await axios.post(
                "http://127.0.0.1:8000/api/marks/", 
                {
                    studentId,
                    ...marks,
                },
                {
                    headers: {
                        Authorization: `Bearer ${token}`, // Include token in Authorization header
                    },
                }
            );
            alert("Marks Added Successfully!");
            navigate("/marks-list");
        } catch (error) {
            console.error("Error adding marks:", error);

            // If token is invalid or expired, attempt to refresh it
            if (error.response?.data?.detail === "Given token not valid for any token type") {
                const newToken = await refreshToken();
                if (newToken) {
                    // Retry the request with the new token
                    await axios.post(
                        "http://127.0.0.1:8000/api/marks/", 
                        {
                            studentId,
                            ...marks,
                        },
                        {
                            headers: {
                                Authorization: `Bearer ${newToken}`,
                            },
                        }
                    );
                    alert("Marks Added Successfully!");
                    navigate("/marks-list");
                } else {
                    alert("Failed to refresh token. Please log in again.");
                }
            } else {
                alert(error.response?.data?.detail || "Failed to add marks. Try again.");
            }
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card p-4 shadow">
                        <h2 className="text-center mb-4">Add Marks</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    required
                                    placeholder="Student ID"
                                    value={studentId}
                                    onChange={(e) => setStudentId(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    required
                                    placeholder="Subject 1 Marks"
                                    value={marks.subject1}
                                    onChange={(e) =>
                                        setMarks({ ...marks, subject1: e.target.value })
                                    }
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    required
                                    placeholder="Subject 2 Marks"
                                    value={marks.subject2}
                                    onChange={(e) =>
                                        setMarks({ ...marks, subject2: e.target.value })
                                    }
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    required
                                    placeholder="Subject 3 Marks"
                                    value={marks.subject3}
                                    onChange={(e) =>
                                        setMarks({ ...marks, subject3: e.target.value })
                                    }
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    required
                                    placeholder="Subject 4 Marks"
                                    value={marks.subject4}
                                    onChange={(e) =>
                                        setMarks({ ...marks, subject4: e.target.value })
                                    }
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    required
                                    placeholder="Subject 5 Marks"
                                    value={marks.subject5}
                                    onChange={(e) =>
                                        setMarks({ ...marks, subject5: e.target.value })
                                    }
                                />
                            </div>
                            <button type="submit" className="btn btn-success w-100">
                                Add Marks
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddMarks;
