import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditMarks = () => {
    const [marks, setMarks] = useState({
        subject1: "",
        subject2: "",
        subject3: "",
        subject4: "",
        subject5: "",
    });
    const { id } = useParams();  // Get the ID from URL parameters
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch existing marks for the student
        const fetchMarks = async () => {
            const response = await axios.get(`http://127.0.0.1:8000/api/marks/${id}/`);
            setMarks(response.data);
        };
        fetchMarks();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://127.0.0.1:8000/api/marks/${id}/`, marks);
            alert("Marks Updated Successfully!");
            navigate("/marks-list");
        } catch (error) {
            console.error("Error updating marks:", error);
            alert("Failed to update marks. Try again.");
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card p-4 shadow">
                        <h2 className="text-center mb-4">Edit Marks</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Subject 1 Marks"
                                    value={marks.subject1}
                                    onChange={(e) => setMarks({ ...marks, subject1: e.target.value })}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Subject 2 Marks"
                                    value={marks.subject2}
                                    onChange={(e) => setMarks({ ...marks, subject2: e.target.value })}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Subject 3 Marks"
                                    value={marks.subject3}
                                    onChange={(e) => setMarks({ ...marks, subject3: e.target.value })}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Subject 4 Marks"
                                    value={marks.subject4}
                                    onChange={(e) => setMarks({ ...marks, subject4: e.target.value })}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Subject 5 Marks"
                                    value={marks.subject5}
                                    onChange={(e) => setMarks({ ...marks, subject5: e.target.value })}
                                />
                            </div>
                            <button type="submit" className="btn btn-warning w-100">
                                Update Marks
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EditMarks;
