// Import required packages
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Signup = () => {
    // State variables for user input
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [groupName, setGroupName] = useState(""); // Changed from group_name to groupName for consistency
    
    const navigate = useNavigate(); // React Router navigation hook

    // Function to handle signup form submission
    const handleSignup = async (e) => {
        e.preventDefault(); // Prevent default form submission

        try {
            const response = await axios.post("http://127.0.0.1:8000/api/auth/signup/", { 
                username, 
                password,
                group_name: groupName, // Ensure correct key is sent to backend
            });

            // Save token in local storage
            localStorage.setItem("token", response.data.token);

            alert("Signup Successful!"); // Show success message

            // Redirect user to dashboard after successful signup
            navigate("/dashboard");
        } catch (error) {
            console.error("Signup Error:", error);
            alert(error.response?.data?.message || "Signup failed! Please try again.");
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-4">
                    <div className="card p-4 shadow">
                        <h2 className="text-center mb-4">Register</h2>
                        <form onSubmit={handleSignup}>
                            <div className="mb-3">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    required 
                                    placeholder="Username" 
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)} 
                                />
                            </div>
                            <div className="mb-3">
                                <input 
                                    type="password" 
                                    className="form-control" 
                                    required 
                                    placeholder="Password" 
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)} 
                                />
                            </div>
                            <div className="mb-3">
                                <select 
                                    className="form-control" 
                                    required 
                                    value={groupName}
                                    onChange={(e) => setGroupName(e.target.value)}
                                >
                                    <option value="">Select Role</option>
                                    <option value="Admin">Admin</option>
                                    <option value="Staff">Staff</option>
                                    <option value="Student">Student</option>
                                </select>
                            </div>
                            <button className="btn btn-success w-100" type="submit">
                                Register
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

// Export Signup component
export default Signup;
