import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddStudent = () => {
  // State variables to store student details
  const [name, setName] = useState("");
  const [age, setAge] = useState("");
  const [department, setDepartment] = useState("");    // Expected: Department ID (number)
  const [admissionId, setAdmissionId] = useState("");  // Expected: Admission ID (number)

  const navigate = useNavigate();

  // Handle form submission to send student data to the backend
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Get token from localStorage
    const token = localStorage.getItem("token");
    if (!token) {
      alert("You are not authenticated. Please log in.");
      navigate("/login");
      return;
    }
  
    // Prepare data for the API call
    const studentData = {
      Name: name,
      Age: age ? parseInt(age, 10) : null,
      Department: department ? parseInt(department, 10) : null,
    };
  
    try {
      // Send the POST request to add a new student (without AdmissionId).
      const response = await axios.post("http://127.0.0.1:8000/api/auth/students/", studentData, {
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Token ${token}`
        }
      });
  
      const newStudentId = response.data.StudentId;
  
      // If AdmissionId is provided, update it later.
      if (admissionId) {
        await axios.patch(`http://127.0.0.1:8000/api/auth/students/${newStudentId}/`, {
          AdmissionId: admissionId
        }, {
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Token ${token}`
          }
        });
      }
  
      alert("Student added successfully!");
      navigate("/dashboard");
    } catch (error) {
      console.error("Error adding student:", error.response?.data || error.message);
      alert("Failed to add student. Please check your input or try again.");
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card p-4 shadow">
            <h2 className="text-center mb-4">Add Student</h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Age"
                  value={age}
                  onChange={(e) => setAge(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Department ID"
                  value={department}
                  onChange={(e) => setDepartment(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <input
                  type="number"
                  className="form-control"
                  placeholder="Admission ID"
                  value={admissionId}
                  onChange={(e) => setAdmissionId(e.target.value)}
                />
              </div>
              <button className="btn btn-primary w-100" type="submit">
                Add Student
              </button>
              <button
                type="button"
                className="btn btn-secondary w-100 mt-2"
                onClick={() => navigate("/dashboard")}
              >
                Back to Dashboard
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddStudent;
