//import required libraries and components
import axios from "axios";
import {useEffect, useState} from "react";
//useEffect is a react hook to perform side effect like fetching data
//useState is a react hook to store state variables or manage states
import {Link} from "react-router-dom";
//Link is a react component to create links for navigation within the app
import { useNavigate } from "react-router-dom";
//react hook to allow redirection of user to another page

const Dashboard = () => {
    //we need two state variables to hold the retrived employees and depts data
    const[employees, setEmployees] = useState([]); //initialize with blank list
    const[departments, setDepartments] = useState([]);
    //useEffect() runs immediately when the Dashboard component mounts or loads
    //and it fetches data from the two backend endpoints /api/employees and /api/departments
        
    //initialize the navigate fn so that we can redirect users to other pages
    const navigate = useNavigate();

    useEffect(()=>{

        const token = localStorage.getItem("token");
        if(!token) {
            navigate("/");
            return;
        }

        //create a custom fn fetchData() to fetch data using the axios library
        //using async()-await will fetch the data asynchoronously
        const fetchData = async() => {
            try {
                const empData = await axios.get("http://127.0.0.1:8000/api/employees/");
                const deptData = await axios.get("http://127.0.0.1:8000/api/departments/");
                //set the state variables using the retrived data
                setEmployees(empData.data);
                setDepartments(deptData.data);
            } catch(error) {
                alert("Error fetching the data");
            }
        };
        fetchData(); //call the fetchData() fn
    },[navigate]);
    
    //log out funcationality
    const handleLogout = ()=>{
    localStorage.removeItem("token");
    navigate("/");//include an empty dependency list
}
    return(

        <div className="container mt-4">
            <h2 className="text-center mb-4">Welcome to the Dashboard</h2>

            <div className="card p-3 mb-4">
                <h3>Employee List:</h3>
                <button className="btn btn-secondary w-25 mt-2" 
                    onClick={()=>navigate("/add-employee")}>Add Employee</button>
                <table className="table table-striped">
                    <thead><tr><th>Name</th><th>Designation</th><th>Department</th><th>Actions</th></tr></thead>
                    <tbody>
                        {employees.map((eachemp)=>(
                            <tr key={eachemp.EmployeeId}>
                                <td>{eachemp.EmployeeName}</td> 
                                <td>{eachemp.Designation}</td>  
                                <td>{eachemp.Department.DepartmentName}</td>
                                <td><Link className="btn btn-primary btn-sm me-2" to={`edit-employee/${eachemp.EmployeeId}`}>Edit</Link></td>
                                <td><Link className="btn btn-danger btn-sm" to={`/delete-employee/${eachemp.EmployeeId}`}>Delete</Link></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div className="card p-3">           
                <h3>Department List:</h3>
                <button className="btn btn-secondary w-25 mt-2" 
                    onClick={()=>navigate("/add-department")}>Add Department</button>
                <table className="table table-striped">
                <thead><tr><th>Department Name</th></tr></thead>
                    <tbody>
                        {departments.map((eachdept)=>(
                            <tr key={eachdept.DepartmentId}>
                                <td>{eachdept.DepartmentName}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

        </div>
    );
};
export default Dashboard;
