// Import the required packages
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddMarks = () => {
    // Initialize state variables to store student marks details
    const [studentID, setStudentID] = useState("");
    const [subject, setSubject] = useState("");
    const [marks, setMarks] = useState("");

    // Initialize the navigate function to redirect users to other pages
    const navigate = useNavigate();

    // Handle form submission to send marks data to the backend
    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent page reload on form submission
        try {
            // Get the token from local storage
            const token = localStorage.getItem("token");

            // Send a POST request to the Add Marks endpoint
            await axios.post(
                "http://localhost:8000/api/marks/",
                {
                    student_id: studentID,
                    subject: subject,
                    marks: marks,
                },
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );

            alert("Marks added successfully");
            // Redirect to dashboard after successful addition
            navigate("/dashboard");
        } catch (error) {
            console.error(error);
            alert("Failed to add marks");
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-4">
                    <div className="card p-4 shadow">
                        <h2 className="text-center">Add Marks</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <input
                                    type="text"
                                    className="form-control"
                                    required
                                    placeholder="Student ID"
                                    onChange={(e) => setStudentID(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="text"
                                    className="form-control"
                                    required
                                    placeholder="Subject"
                                    onChange={(e) => setSubject(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input
                                    type="number"
                                    className="form-control"
                                    required
                                    placeholder="Marks"
                                    onChange={(e) => setMarks(e.target.value)}
                                />
                            </div>
                            <button className="btn btn-primary w-100" type="submit">
                                Add Marks
                            </button>
                            <button
                                className="btn btn-secondary w-100 mt-2"
                                onClick={() => navigate("/dashboard")}
                            >
                                Back to Dashboard
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddMarks;
