import { jwtDecode } from "jwt-decode";
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddStudent = () => {
    // Initializing state variables to store student details
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [dateOfBirth, setDateOfBirth] = useState("");
    const [enrollmentDate, setEnrollmentDate] = useState("");

    // Initialize the navigate function to redirect users to other pages
    const navigate = useNavigate();

    // Handle form submission to send student data to the backend
    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent page reload on form submission

        // Get token from localStorage
        const token = localStorage.getItem("access_token") || localStorage.getItem("token");
        
        console.log("Token:", token);

        if (!token) {
            alert("Token not found. Please log in again.");
            navigate("/login");
            return;
        }

        try {
            // Decode token and check expiration
            const decoded = jwtDecode(token);
            const currentTime = Date.now() / 1000;
            if (decoded.exp < currentTime) {
                alert("Your session has expired. Please log in again.");
                localStorage.removeItem("token");
                navigate("/login");
                return;
            }

            // Send the POST request to add a student
            await axios.post("http://localhost:8000/api/students/", {
                first_name: firstName,  
                last_name: lastName, 
                date_of_birth: dateOfBirth,
                enrollment_date: enrollmentDate,
            }, {
                headers: { "Authorization": `Bearer ${token}` },
            });

            alert("Student added successfully");
            navigate("/dashboard");
        } catch (error) {
            console.error("Error:", error);
            alert("Failed to add student");
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-4">
                    <div className="card p-4 shadow">
                        <h2 className="text-center">Add Student</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    required 
                                    placeholder="First Name" 
                                    value={firstName}
                                    onChange={(e) => setFirstName(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    required 
                                    placeholder="Last Name" 
                                    value={lastName}
                                    onChange={(e) => setLastName(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input 
                                    type="date" 
                                    className="form-control" 
                                    required 
                                    placeholder="Date of Birth" 
                                    value={dateOfBirth}
                                    onChange={(e) => setDateOfBirth(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input 
                                    type="date" 
                                    className="form-control" 
                                    required 
                                    placeholder="Enrollment Date" 
                                    value={enrollmentDate}
                                    onChange={(e) => setEnrollmentDate(e.target.value)}
                                />
                            </div>
                            <button className="btn btn-primary w-100" type="submit">Add Student</button>
                            <button className="btn btn-secondary w-100 mt-2" onClick={() => navigate("/dashboard")}>
                                Back to Dashboard
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddStudent;
