import { useState } from "react";

const MarksEntry = () => {
  const [student, setStudent] = useState("");
  const [subject, setSubject] = useState("");
  const [marksObtained, setMarksObtained] = useState("");
  const [totalMarks, setTotalMarks] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = { student, subject, marks_obtained: marksObtained, total_marks: totalMarks };
    await fetch("/api/marks/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={student} onChange={(e) => setStudent(e.target.value)} placeholder="Student" />
      <input type="text" value={subject} onChange={(e) => setSubject(e.target.value)} placeholder="Subject" />
      <input type="number" value={marksObtained} onChange={(e) => setMarksObtained(e.target.value)} placeholder="Marks Obtained" />
      <input type="number" value={totalMarks} onChange={(e) => setTotalMarks(e.target.value)} placeholder="Total Marks" />
      <button type="submit">Add Marks</button>
    </form>
  );
};

export default MarksEntry;
