import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Dashboard = () => {
    // State variables for employees, departments, and marks data
    const [employees, setEmployees] = useState([]);
    const [departments, setDepartments] = useState([]);
    const [marks, setMarks] = useState([]); // New state for marks data

    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/");
            return;
        }

        // Fetch data for employees, departments, and marks
        const fetchData = async () => {
            try {
                const empData = await axios.get("http://127.0.0.1:8000/api/employees/");
                const deptData = await axios.get("http://127.0.0.1:8000/api/departments/");
                const marksData = await axios.get("http://127.0.0.1:8000/api/marks/"); // Fetch marks data
                setEmployees(empData.data);
                setDepartments(deptData.data);
                setMarks(marksData.data); // Set marks data
            } catch (error) {
                alert("Error fetching the data");
            }
        };

        fetchData();
    }, [navigate]);

    // Log out functionality
    const handleLogout = () => {
        localStorage.removeItem("token");
        navigate("/");
    };

    return (
        <div className="container mt-4">
            <h2 className="text-center mb-4">Welcome to the Dashboard</h2>

            <div className="card p-3 mb-4">
                <h3>Employee List:</h3>
                <button
                    className="btn btn-secondary w-25 mt-2"
                    onClick={() => navigate("/add-employee")}
                >
                    Add Employee
                </button>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Designation</th>
                            <th>Department</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {employees.map((eachemp) => (
                            <tr key={eachemp.EmployeeId}>
                                <td>{eachemp.EmployeeName}</td>
                                <td>{eachemp.Designation}</td>
                                <td>{eachemp.Department.DepartmentName}</td>
                                <td>
                                    <Link
                                        className="btn btn-primary btn-sm me-2"
                                        to={`edit-employee/${eachemp.EmployeeId}`}
                                    >
                                        Edit
                                    </Link>
                                </td>
                                <td>
                                    <Link
                                        className="btn btn-danger btn-sm"
                                        to={`/delete-employee/${eachemp.EmployeeId}`}
                                    >
                                        Delete
                                    </Link>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div className="card p-3 mb-4">
                <h3>Department List:</h3>
                <button
                    className="btn btn-secondary w-25 mt-2"
                    onClick={() => navigate("/add-department")}
                >
                    Add Department
                </button>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Department Name</th>
                        </tr>
                    </thead>
                    <tbody>
                        {departments.map((eachdept) => (
                            <tr key={eachdept.DepartmentId}>
                                <td>{eachdept.DepartmentName}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            <div className="card p-3">
                <h3>Marks List:</h3>
                <button
                    className="btn btn-secondary w-25 mt-2"
                    onClick={() => navigate("/add-marks")}
                >
                    Add Marks
                </button>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Student ID</th>
                            <th>Subject 1</th>
                            <th>Subject 2</th>
                            <th>Subject 3</th>
                            <th>Subject 4</th>
                            <th>Subject 5</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {marks.map((mark) => (
                            <tr key={mark.id}>
                                <td>{mark.studentId}</td>
                                <td>{mark.subject1}</td>
                                <td>{mark.subject2}</td>
                                <td>{mark.subject3}</td>
                                <td>{mark.subject4}</td>
                                <td>{mark.subject5}</td>
                                <td>
                                    <Link
                                        className="btn btn-primary btn-sm me-2"
                                        to={`edit-marks/${mark.id}`}
                                    >
                                        Edit
                                    </Link>
                                </td>
                                <td>
                                    <Link
                                        className="btn btn-danger btn-sm"
                                        to={`/delete-marks/${mark.id}`}
                                    >
                                        Delete
                                    </Link>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Dashboard;
