// Import the required packages
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddStudent = () => {
    // Initializing state variables to store student details
    const [studentName, setStudentName] = useState("");
    const [studentID, setStudentID] = useState("");
    const [course, setCourse] = useState("");

    // Initialize the navigate function to redirect users to other pages
    const navigate = useNavigate();

    // Handle form submission to send student data to the backend
    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent page reload on form submission
        try {
            // Get the token from local storage
            const token = localStorage.getItem("token");

            // Send a POST request to the add student endpoint
            await axios.post("http://localhost:8000/api/students/", {
                name: studentName,
                student_id: studentID,
                course: course,
            }, {
                headers: { Authorization: `Bearer ${token}` },
            });

            alert("Student added successfully");
            navigate("/dashboard"); // Redirect to dashboard after successful addition
        } catch (error) {
            console.error(error);
            alert("Failed to add student");
        }
    };

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-4">
                    <div className="card p-4 shadow">
                        <h2 className="text-center">Add Student</h2>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    required 
                                    placeholder="Student Name" 
                                    onChange={(e) => setStudentName(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    required 
                                    placeholder="Student ID" 
                                    onChange={(e) => setStudentID(e.target.value)}
                                />
                            </div>
                            <div className="mb-3">
                                <input 
                                    type="text" 
                                    className="form-control" 
                                    required 
                                    placeholder="Course" 
                                    onChange={(e) => setCourse(e.target.value)}
                                />
                            </div>
                            <button className="btn btn-primary w-100" type="submit">Add Student</button>
                            <button className="btn btn-secondary w-100 mt-2" onClick={() => navigate("/dashboard")}>
                                Back to Dashboard
                            </button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddStudent;
