import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditMark = () => {
  const { id } = useParams(); // Get the mark ID from the URL
  const navigate = useNavigate();
  const [markData, setMarkData] = useState({
    student: "",
    subject: "",
    marks: "",
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    // Fetch the existing mark details
    const fetchMark = async () => {
      try {
        const res = await axios.get(`http://127.0.0.1:8000/api/marks/${id}/`, {
          headers: {
            Authorization: `Token ${token}`,
          },
        });
        setMarkData(res.data);
        setLoading(false);
      } catch (error) {
        setError("Error fetching mark details.");
        setLoading(false);
      }
    };

    fetchMark();
  }, [id, navigate]);

  const handleChange = (e) => {
    setMarkData({ ...markData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    try {
      await axios.put(`http://127.0.0.1:8000/api/marks/${id}/`, markData, {
        headers: {
          Authorization: `Token ${token}`,
          "Content-Type": "application/json",
        },
      });
      alert("Mark updated successfully!");
      navigate("/dashboard");
    } catch (error) {
      setError("Error updating mark.");
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="container mt-5">
      <h2>Edit Mark</h2>

      {error && <div className="alert alert-danger">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Student Name</label>
          <input
            type="text"
            className="form-control"
            name="student"
            value={markData.student}
            onChange={handleChange}
            disabled
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Subject</label>
          <input
            type="text"
            className="form-control"
            name="subject"
            value={markData.subject}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label className="form-label">Marks</label>
          <input
            type="number"
            className="form-control"
            name="marks"
            value={markData.marks}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary">
          Update Mark
        </button>
        <button
          type="button"
          className="btn btn-secondary ms-3"
          onClick={() => navigate("/dashboard")}
        >
          Cancel
        </button>
      </form>
    </div>
  );
};

export default EditMark;
